CREATE  TABLE IF NOT EXISTS `ELEMENT` (  `id` INT NOT NULL ,  `name` VARCHAR(90) NOT NULL ,  `fat` DOUBLE NULL ,  `fiber` DOUBLE NULL ,  `carbs` DOUBLE NULL ,  `kcal` DOUBLE NULL ,  `protein` DOUBLE NULL ,  `ww` DOUBLE NULL ,  `wbt` DOUBLE NULL ,  `wm` DOUBLE NULL ,  `wbt_proc` INT NULL ,  `type` INT NOT NULL ,  `is_favourite` TINYINT(1) NULL DEFAULT false ,  `weight_cooked` DOUBLE NULL DEFAULT NULL ,  `is_user_defined` TINYINT(1) NULL DEFAULT false ,  `img_name` VARCHAR(45) NULL DEFAULT NULL ,  `date_created` DATETIME NULL ,  `weight_meal` DOUBLE NULL ,  PRIMARY KEY (`id`) );
CREATE  TABLE IF NOT EXISTS `CATEGORY` (  `id` INT NOT NULL ,  `name` VARCHAR(45) NOT NULL ,  `type` INT NOT NULL ,  `depth` INT NULL DEFAULT 0 ,  `is_user_defined` TINYINT(1) NOT NULL DEFAULT false ,  PRIMARY KEY (`id`) );
CREATE  TABLE IF NOT EXISTS `ELEMENT_HAS_ELEMENT` (  `id_child` INT NOT NULL ,  `id_parent` INT NOT NULL ,  `weight` FLOAT NOT NULL ,  PRIMARY KEY (`id_child`, `id_parent`) ,  CONSTRAINT `fk_ELEMENT_has_ELEMENT_ELEMENT`    FOREIGN KEY (`id_child` )    REFERENCES `ELEMENT` (`id` )    ON DELETE NO ACTION    ON UPDATE NO ACTION,  CONSTRAINT `fk_ELEMENT_has_ELEMENT_ELEMENT1`    FOREIGN KEY (`id_parent` )    REFERENCES `ELEMENT` (`id` )    ON DELETE NO ACTION    ON UPDATE NO ACTION);
CREATE  INDEX `fk_ELEMENT_has_ELEMENT_ELEMENT1` ON  ELEMENT_HAS_ELEMENT(`id_parent` ASC) ;
CREATE  INDEX `fk_ELEMENT_has_ELEMENT_ELEMENT` ON  ELEMENT_HAS_ELEMENT(`id_child` ASC) ;
CREATE  TABLE IF NOT EXISTS `ELEMENT_HAS_CATEGORY` (  `id_element` INT NOT NULL ,  `id_category` INT NOT NULL ,  PRIMARY KEY (`id_element`, `id_category`) ,  CONSTRAINT `fk_ELEMENT_has_CATEGORY_ELEMENT1`    FOREIGN KEY (`id_element` )    REFERENCES `ELEMENT` (`id` )    ON DELETE NO ACTION    ON UPDATE NO ACTION,  CONSTRAINT `fk_ELEMENT_has_CATEGORY_CATEGORY1`    FOREIGN KEY (`id_category` )    REFERENCES `CATEGORY` (`id` )    ON DELETE NO ACTION    ON UPDATE NO ACTION);
CREATE INDEX `fk_ELEMENT_has_CATEGORY_CATEGORY1` ON ELEMENT_HAS_CATEGORY(`id_category` ASC) ;
CREATE  INDEX `fk_ELEMENT_has_CATEGORY_ELEMENT1` ON ELEMENT_HAS_CATEGORY(`id_element` ASC) ;
CREATE  TABLE IF NOT EXISTS `CATEGORY_HAS_CATEGORY` (  `id_child` INT NOT NULL ,  `id_parent` INT NOT NULL ,  PRIMARY KEY (`id_child`, `id_parent`) ,  CONSTRAINT `fk_CATEGORY_has_CATEGORY_CATEGORY1`    FOREIGN KEY (`id_child` )    REFERENCES `CATEGORY` (`id` )    ON DELETE NO ACTION    ON UPDATE NO ACTION,  CONSTRAINT `fk_CATEGORY_has_CATEGORY_CATEGORY2`    FOREIGN KEY (`id_parent` )    REFERENCES `CATEGORY` (`id` )    ON DELETE NO ACTION    ON UPDATE NO ACTION);
CREATE INDEX `fk_CATEGORY_has_CATEGORY_CATEGORY2` ON CATEGORY_HAS_CATEGORY(`id_parent` ASC) ;
CREATE INDEX `fk_CATEGORY_has_CATEGORY_CATEGORY1` ON CATEGORY_HAS_CATEGORY(`id_child` ASC);
